local Addon, private = ...local print = printlocal string = stringlocal table = tablelocal unpack = unpacklocal Command = Commandlocal shortcuts = {	i = "inventory",	inv = "inventory",	b = "bank",}local allowedLocations = {	inventory = true,	bank = true,}setfenv(1, private)local function slashMain(args)	local char, location = unpack(string.split(args, "%s", true))	if(not (char and location)) then		print("\n/imhobags " .. args)		print(L.SlashMessage.usage)		return	end	if(not ItemDB.CharacterExists(char)) then		print("\n/imhobags " .. args)		print(string.format(L.SlashMessage.unknownChar, char))		return	end	local loc = shortcuts[location] or location	if(not allowedLocations[loc]) then		print("\n/imhobags " .. args)		print(string.format(L.SlashMessage.unknownLocation, location))		return	end	Ux.ShowItemWindow(char, loc)endeventTable = Command.Slash.Register("imhobags")table.insert(eventTable, { slashMain, Addon.identifier, "slashMain" })