local Addon, private = ...local print = printlocal string = stringlocal table = tablelocal unpack = unpacklocal Command = Commandlocal shortcuts = {	i = "inventory",	inv = "inventory",	b = "bank",	m = "mail",}local allowedLocations = {	inventory = true,	bank = true,	mail = true,}setfenv(1, private)local function slashMain(args)	local arg1, arg2 = unpack(string.split(args, "%s", true))		-- First version: /imhobags search	if(arg1 and arg1 == "search") then		Ux.SearchWindow:Show()		return	end		-- Second version: /imhobags char location	if(not (arg1 and arg2)) then		print("\n/imhobags " .. args)		print(L.SlashMessage.usage)		return	end	if(not ItemDB.CharacterExists(arg1)) then		print("\n/imhobags " .. args)		print(string.format(L.SlashMessage.unknownChar, arg1))		return	end	local loc = shortcuts[arg2] or arg2	if(not allowedLocations[loc]) then		print("\n/imhobags " .. args)		print(string.format(L.SlashMessage.unknownLocation, arg2))		return	end	Ux.ShowItemWindow(arg1, loc)endeventTable = Command.Slash.Register("imhobags")table.insert(eventTable, { slashMain, Addon.identifier, "slashMain" })