local identifier = (...).idlocal addon = (...).data-- Builtinslocal ipairs = ipairslocal pairs = pairslocal pcall = pcalllocal table = tablelocal setmetatable = setmetatable-- Localslocal rarityOrder = {	sellable = 1,	common = 2,	uncommon = 3,	rare = 4,	epic = 5,	relic = 6,	transcendant = 7,	quest = 8,}setfenv(1, addon)Sort = Sort or { }Sort.Default = { }-- Public methods-- ============================================================================function Sort.Default.ByItemName(type1, type2)	return type1.name < type2.nameendfunction Sort.Default.ByItemRarity(type1, type2)	local r1 = type1.rarity or "common"	local r2 = type2.rarity or "common"	return rarityOrder[r1] < rarityOrder[r2]end