local ipairs = ipairslocal print = printlocal table = tablelocal UI = UI-- Frames cannot be deleted, keep a cache and only create new frames if the cache is emptylocal cachedCategories = { }setfenv(1, ImhoBags)Ux = Ux or { }ItemWindowMarginTop = 60ItemWindowMargin = 20ItemWindowWidth = 473-- Private methods-- ============================================================================local function createCategoryLabel(window)	local text	if(#cachedCategories == 0) then		text = UI.CreateFrame("Text", "", window)		text:SetFontSize(14)	else		text = table.remove(cachedCategories)	end	return textendlocal function clear(self)	-- Clear all categories and buttons and return them to cache	for _, cat in ipairs(self.categories) do		cat:SetVisible(false)		table.insert(cachedCategories, cat)	end	for _, btn in ipairs(self.buttons) do		btn:Dispose()	end	self.categories = { }	self.buttons = { }endlocal function window_UpdateItems(self, items)	clear(self)	self.items = items		local marginTop = ItemWindowMarginTop	local margin = ItemWindowMargin	local x, y = margin, marginTop	local dx, dy = ItemButtonWidth, ItemButtonHeight		for _, cat in ipairs(items) do		if(x > margin) then			x = margin			y = y + dy		end		local label = createCategoryLabel(self)		table.insert(self.categories, label)		label:SetText(cat.name)		label:SetHeight(label:GetFullHeight())		label:SetWidth(self:GetWidth() - margin)		label:SetPoint("TOPLEFT", self, "TOPLEFT", x, y)		y = y + label:GetHeight()				for _, item in ipairs(cat) do			local button = Ux.CreateItemButton(self)			table.insert(self.buttons, button)			if(x + dx > self:GetWidth() - margin) then				x = margin				y = y + dy			end			button:SetPoint("TOPLEFT", self, "TOPLEFT", x, y)			button:SetItem(item.type, item.slots, item.stack)			x = x + dx		end	end		self:SetHeight(y + margin + dy)end-- Public methods-- ============================================================================function CreateItemWindow(title)	local window = UI.CreateFrame("RiftWindow", "ImhoBags_ItemWindow_"..title, Context)	window.buttons = { }	window.categories = { }	window.items = { }		window:SetTitle(title)	window:SetWidth(ItemWindowWidth)		local closeButton = UI.CreateFrame("RiftButton", "", window)	closeButton:SetSkin("close")	closeButton:SetPoint("TOPRIGHT", window, "TOPRIGHT", -7, 16)	function closeButton.Event:LeftClick()		window:SetVisible(false)	end		window.UpdateItems = window_UpdateItems		return windowend