local Addon, private = ...local _G = _Glocal ipairs = ipairslocal getmetatable = getmetatablelocal math = mathlocal string = stringlocal table = tablelocal type = typelocal dump = dumplocal Command = Commandlocal Event = Eventlocal Inspect = Inspectlocal UI = UIsetfenv(1, private)Ux = Ux or { }Ux.ItemWindowBase = { }Ux.ItemWindowColumns = 8Ux.ItemWindowPadding = 4Ux.ItemWindowCellSpacing = 2Ux.ItemWindowMinWidth = 345 -- Prevents header buttons from overlapping and is lower bound for full columnsUx.ItemWindowMinHeight = 380 -- Title bar stats resizing below this size-- Private methods-- ============================================================================local function clearItemDisplay(self)	-- Clear all groups and buttons and return them to cache	for _, label in ipairs(self.groupLabels) do		label:Dispose()	end	for _, btn in ipairs(self.buttons) do		btn:Dispose()	end	self.groupLabels = { }	self.buttons = { }endlocal function isNotLocked(window)	-- Non-player characters always report a number as empty slots.	-- Whereas the player gets a list of all empty slots--@debug@	return type(window.empty) == "table" and window.interaction--@end-debug@--[===[@non-debug@	return false--@end-non-debug@]===]endlocal function getItems(self)	local success	self.items, self.empty, success = self.matrix:GetUnsortedItems(self.condensed)	if(success) then		self.getItemFailed = 0	else		self.getItemFailed = self.getItemFailed + 1	end	self:getGroups()endlocal function closeButton_LeftPress(self)	local window = self:GetParent()	window:SetVisible(false)	log("TODO", "close the native frame(s)")endlocal function systemUpdateBegin(self)	-- Inspect.Time.Frame() is not good enough and can cause multiple updates per frame	local now = Inspect.Time.Real()	if(self.matrix.lastUpdate >= self.lastUpdate) then		getItems(self)		self:Update()		self.lastUpdate = now		log("update", self:GetTitle(), self.lastUpdate)	elseif(self.getItemFailed > 0 and (now - self.lastUpdate > 1)) then		log("getItemFailed", self:GetTitle(), self.getItemFailed)		getItems(self)		-- Limit the number of retries to avoid permanent performance drops		if(self.getItemFailed > 3) then			log("getItemFailed", self:GetTitle(), "giving up")			self.getItemFailed = 0		end		self:Update()		self.lastUpdate = now		log("update", self:GetTitle(), self.lastUpdate)	endendlocal function hookToNative(window, native)	if(not native) then		return	end	-- This will not work if other addons try to do the same	function native.Event:Loaded()		window:SetVisible(self:GetLoaded())		window:SetCharacter(PlayerName, window.location)		log("TODO", "move native frame(s) out of screen")	end	window.native = native	window:SetVisible(native:GetLoaded()) -- Initially visible?endlocal function applySearchFilter(self)	if(self.searchString == "") then		for _, button in ipairs(self.buttons) do			button.icon:SetAlpha(1.0)		end	else		local lower = string.lower		local find = string.find		for _, button in ipairs(self.buttons) do			if(find(lower(button.type.name), self.searchString, 1, true)) then				button.icon:SetAlpha(1.0)			else				button.icon:SetAlpha(0.3)			end		end	endendlocal function content_MouseMove(self)	local mouse = Inspect.Mouse()	if(self.sizingOffset) then		self.window:SetWidth(math.max(Ux.ItemWindowMinWidth, mouse.x - self.window:GetLeft() + self.sizingOffset))		self.window:Update()	elseif(self.mouseOffsetX) then		self.window:SetPoint("TOPLEFT", _G.UIParent, "TOPLEFT", mouse.x - self.mouseOffsetX, mouse.y - self.mouseOffsetY)	endendlocal function content_LeftDown(self)	local mouse = Inspect.Mouse()	local left, top, right, bottom = self.window:GetTrimDimensions()	if(mouse.x > self.window:GetRight() - right - Ux.ItemWindowPadding and mouse.y > self.window:GetTop() + top) then		self.sizingOffset = self.window:GetRight() - mouse.x	else		self.mouseOffsetX = mouse.x - self.window:GetLeft()		self.mouseOffsetY = mouse.y - self.window:GetTop()	endendlocal function content_LeftUpoutside(self)	self.mouseOffsetX, self.mouseOffsetY = nil, nil	self.sizingOffset = nilendlocal function content_LeftUp(self)	content_LeftUpoutside(self)	-- Drop item	self.window:leftUp()endlocal function interactionChanged(self, interaction, state)	if(interaction == self.location) then		self.interaction = state		self:Update()	endendlocal function filter_KeyFocusGain(self, window)	if(self:GetText() == L.Ux.search) then		self:SetText("")	endendlocal function filter_KeyFocusLoss(self, window)	if(self:GetText() == "") then		self:SetText(L.Ux.search)	endendlocal function filter_TextfieldChange(self, window)	window.searchString = string.lower(self:GetText())	applySearchFilter(window)endlocal function createIconButton(parent, data, icon)	local btn = UI.CreateFrame("Texture", "", parent)	btn:SetTexture(data, icon)	btn:SetWidth(32)	btn:SetHeight(32)	btn:SetAlpha(0.7)	function btn.Event:MouseIn() self:SetAlpha(1.0) end	function btn.Event:MouseOut() self:SetAlpha(0.7) end	return btnend-- Protected methods-- ============================================================================-- return x, y, sell, slotslocal function renderItems(self, items, left, x, y, width, dx, dy, spacing, content, notLocked)	local sell = 0	local slots = 0	for _, item in ipairs(items) do		local button = Ux.ItemButton.New(self)		table.insert(self.buttons, button)		if(x + dx > width) then			x = left			y = y + dy + spacing		end		button:SetPoint("TOPLEFT", content, "TOPLEFT", x, y)		button:SetItem(item.type, item.slots, item.stack, notLocked)		sell = sell + item.stack * (item.type.sell or 0)		if(type(item.slots) == "table") then			slots = slots + #item.slots		else			slots = slots + item.slots		end		x = x + dx + spacing	end	return x, y, sell, slotsend-- Public methods-- ============================================================================local function ItemWindowBase_SetCharacter(self, character, location)	self.getItemFailed = 0	self.matrix = ItemDB.GetItemMatrix(character, location)	self.character = character	self.location = location	self.lastUpdate = -2	self.charSelector:SetText((character == "player" and PlayerName) or character)	self:setCharacter()endlocal function ItemWindowBase_Update(self)	clearItemDisplay(self)		local notLocked = isNotLocked(self)	local content = self:GetContent()	local width = math.ceil(content:GetWidth() - Ux.ItemWindowPadding)		local left = Ux.ItemWindowPadding	local top = self.contentOffset		local x, y = left, top	local dx, dy = Ux.ItemButtonWidth, Ux.ItemButtonHeight	local spacing = Ux.ItemWindowCellSpacing		for group, items in self:iterateGroups() do		if(x > left) then			x = left			y = y + dy + spacing		end		local label = self:getGroupLabel(group)		table.insert(self.groupLabels, label)		label:SetWidth(width - Ux.ItemWindowPadding)		label:SetPoint("TOPLEFT", content, "TOPLEFT", x, y)		y = y + label:GetHeight() + spacing				local sell, slots		x, y, sell, slots = renderItems(self, items, left, x, y, width, dx, dy, spacing, content, notLocked)		label:SetInfo(sell, slots)	end		local left, top, right, bottom = self:GetTrimDimensions()	self:SetHeight(math.max(Ux.ItemWindowMinHeight, top + y + dy + bottom + Ux.ItemWindowPadding))	-- Display lock item and dim frame if item commands are not allowed	if(notLocked) then--		self:SetAlpha(1.0)		self.readonlyLock:SetVisible(false)	else--		self:SetAlpha(0.75)		self.readonlyLock:SetVisible(true)	end		applySearchFilter(self)endfunction Ux.ItemWindowBase.New(title, character, location, condensed, native)	local self = UI.CreateFrame("RiftWindow", "ImhoBags_ItemWindow_"..location, Ux.Context)	self.title = title	self:SetTitle(title)	local left, top, right, bottom = self:GetTrimDimensions()	local width = Ux.ItemWindowColumns * Ux.ItemButtonWidth + left + right + 2 * Ux.ItemWindowPadding	width = width + (Ux.ItemWindowColumns - 1) * Ux.ItemWindowCellSpacing	self:SetWidth(width)		-- Close button	Ux.RiftWindowCloseButton.New(self, closeButton_LeftPress)		-- Char selector	self.charSelector = Ux.CharSelector.New(self, ItemDB.GetAvailableCharacters(), Inspect.Unit.Detail("player").name, function(char)		self:SetCharacter(char, self.location)	end)	self.charSelector:SetPoint("TOPLEFT", self:GetContent(), "TOPLEFT")		-- Readonly indicator	self.readonlyLock = UI.CreateFrame("Texture", "", self)	self.readonlyLock:SetPoint("LEFTCENTER", self.charSelector, "RIGHTCENTER", -5, -7)	self.readonlyLock:SetWidth(36)	self.readonlyLock:SetHeight(36)	self.readonlyLock:SetTexture(Addon.identifier, "textures/lock_silver.png")		-- Tool buttons	self.searchButton = createIconButton(self, "Rift", [[Data/\UI\item_icons\intact_shambler_eye.dds]])	self.searchButton:SetPoint("TOPRIGHT", self:GetContent(), "TOPRIGHT", -2 * Ux.ItemWindowPadding, -Ux.ItemWindowPadding)	function self.searchButton.Event.LeftDown()		Ux.SearchWindow:Show()	end		self.mailButton = createIconButton(self, "Rift", [[Data/\UI\item_icons\collection_of_love_letters.dds]])	self.mailButton:SetPoint("TOPRIGHT", self.searchButton, "TOPLEFT")	function self.mailButton.Event.LeftDown()		Ux.ShowItemWindow(self.charSelector:GetText(), "mail")	end		self.bankButton = createIconButton(self, "Rift", [[Data/\UI\item_icons\Chest2.dds]])	self.bankButton:SetPoint("TOPRIGHT", self.mailButton, "TOPLEFT")	function self.bankButton.Event.LeftDown()		Ux.ShowItemWindow(self.charSelector:GetText(), "bank")	end	-- Condensed checkbox	self.condensedCeck = Ux.Checkbox.New(self, "Condensed", "LEFT")	self.condensedCeck:SetPoint("TOPRIGHT", self.searchButton, "BOTTOMRIGHT", 0, 3)	self.condensedCeck:SetChecked(condensed)	self.condensed = condensed	function self.condensedCeck.Event:CheckboxChange()		self.condensed = self:GetChecked()		self:SetCharacter(self.character, self.location)	end		-- Search filter	local filter = Ux.Textfield.New(self, "RIGHT", L.Ux.search)	filter:SetPoint("TOPLEFT", self.charSelector, "BOTTOMLEFT", Ux.ItemWindowPadding, -3)	filter:SetPoint("BOTTOMRIGHT", self.condensedCeck, "TOPLEFT", -Ux.ItemWindowPadding - self.condensedCeck.text:GetWidth(), 23)	filter.text.Event.KeyFocusGain = function() filter_KeyFocusGain(filter.text, self) end	filter.text.Event.KeyFocusLoss = function() filter_KeyFocusLoss(filter.text, self) end	filter.text.Event.TextfieldChange = function() filter_TextfieldChange(filter.text, self) end	self.searchString = ""	-- General initialization--@alpha@	hookToNative(self, native)--@end-alpha@	self:SetVisible(false)		local content = self:GetContent()	content.window = self	content.Event.MouseMove = content_MouseMove	content.Event.LeftDown = content_LeftDown	content.Event.LeftUp = content_LeftUp	local border = self:GetBorder()	border.window = self	border.Event.MouseMove = content_MouseMove	border.Event.LeftDown = content_LeftDown	border.Event.LeftUp = content_LeftUp	border.Event.LeftUpoutside = content_LeftUpoutside		self.buttons = { }	self.groupLabels = { }	self.contentOffset = self.charSelector:GetHeight() + filter:GetHeight() + 5		-- Protected abstract methods	self.getGroups = nil	self.leftUp = nil	self.setCharacter = nil	self.getGroupLabel = nil	self.iterateGroups = nil -- return group, items. group can be anything		-- Public methods	self.SetCharacter = ItemWindowBase_SetCharacter	self.Update = ItemWindowBase_Update	table.insert(Event.System.Update.Begin, { function() systemUpdateBegin(self) end, Addon.identifier, "ItemWindowBase_systemUpdateBegin" })		-- If no interaction flag for this location exists it is always available	local interactions = Inspect.Interaction()	if(interactions[location] ~= nil) then		self.interaction = interactions[location]		table.insert(Event.Interaction, { function(...) interactionChanged(self, ...) end, Addon.identifier, "ItemWindowBase_interactionChanged" })	else		self.interaction = true	end	return selfend