local Addon, private = ...setfenv(1, private)Ux = Ux or { }Ux.CurrencyWindow = { }-- Private methods-- ============================================================================-- Public methods-- ============================================================================function Ux.CurrencyWindow.New(title, character, location, itemSize)	local self = Ux.EquipmentWindow.New(title, character, location, itemSize)		if(location == "currency") then		self.currencyButton:SetIcon([[Data/\UI\item_icons\bag20.dds]])		self.currencyButton:SetTooltip(L.Ux.WindowTitle.inventory)		function self.currencyButton.LeftPress()			Ux.ToggleItemWindow(self.charSelector:GetText(), "inventory")		end	end		self.groupFunc = Group.Default.GetCurrencyGroup		self:SetCharacter(character, location)		return selfend